import React, { useState } from "react";
import { Button, Table, Container, Row, Col } from "react-bootstrap";
import "./estilos/tabela.css";
import 'bootstrap-icons/font/bootstrap-icons.css';

export default function TabelaEmprestimo(props) {

    const [termoDeBusca, setTermoDeBusca] = useState('');
    const [exemplar, setExemplar] = useState([]);
    const [acervoLista, setAcervoLista] = useState([]);

    return (
        <body id="corpo" className="colorwhite ">
            <Container className="border mb-2 mt-2 corpoTabela" >
                <h2 className="text-center m-4">Empréstimos Cadastrados</h2>
                <Row className='mb-2 mt-2 '>
                    <Col>
                        <Button variant="success"
                            onClick={() => {
                                props.exibirTabela(false);
                                props.setModoEdicao(false);
                            }}>
                            Cadastrar
                        </Button>
                    </Col>
                </Row>

                <Table striped bordered hover>
    <thead className="colorwhite">
        <tr>
            <th>Código</th>
            <th>Data de Empréstimo</th>
            <th>Pessoa</th>
            <th>Exemplares</th>
            <th>Ações</th>
        </tr>
    </thead>
    <tbody>
        {
            listaEmprestimos?.map((emprestimo) => {
                return (
                    <tr key={emprestimo.codigo}>
                        <td>{emprestimo.codigo}</td>
                        <td>{emprestimo.dataEmprestimo}</td>
                        <td>{emprestimo.pessoa.nome}</td>
                        <td>
                            {emprestimo.listaExemplar.map((exemplar) => (
                                <div key={exemplar.codigo}>{exemplar.titulo}</div>
                            ))}
                        </td>
                        <td>
                            <Button onClick={() => prepararEmprestimoParaEdicao(emprestimo)}>
                                Editar
                            </Button>
                        </td>
                    </tr>
                );
            })
        }
    </tbody>
</Table>
            </Container>
        </body>
    );
}
