import React, { useState, useEffect } from "react";
import { Spinner } from "react-bootstrap";
import Pagina from "../templates/componentes/Pagina.js";
import TabelaEmprestimo from "../tabelas/TabelaEmprestimo.jsx";
import FormEmprestimo from "../Formularios/FormEmprestimo.jsx";
import { urlBase } from "../utilitarios/definicoes.js";

export default function TelaFormEmprestimo(props) {

    const [exibirTabela, setExibirTabela] = useState(true);
    const [emprestimos, setEmprestimos] = useState([]);
    const [modoEdicao, setModoEdicao] = useState(false);
    const [erro, setErro] = useState(null);
    const [processado, setProcessado] = useState(false);
    const [emprestimoEmEdicao, setEmprestimoEmEdicao] = useState({
        codigo: "",
        dataEmprestimo: "",
        pessoa: {
            cpf: "",
        },
        listaExemplares: [],
        itemDisp: "",
    });

    function prepararEmprestimoParaEdicao(emprestimo) {
        setModoEdicao(true);
        setEmprestimoEmEdicao({
            codigo: emprestimo.codigo,
            dataEmprestimo: emprestimo.dataEmprestimo,
            pessoa: {
                cpf: props.pessoaSelecionada.cpf,
            },
            listaExemplares: props.listaItens
        });
        setExibirTabela(false);
    }

    function apagarEmprestimo(emprestimo) {
        fetch(urlBase + "/emprestimo", {
            method: "DELETE",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(emprestimo),
        })
            .then((resposta) => {
                return resposta.json();
            })
            .then((retorno) => {
                if (retorno.resultado) {
                    alert("Não foi possível excluir o empréstimo");
                } else {
                    buscarEmprestimos();
                }
            });
    }

    function buscarEmprestimos() {
        fetch(urlBase + "/emprestimo", {
            method: "GET",
        })
            .then((resposta) => {
                if (!resposta.ok) {
                    throw new Error('Erro na requisição');
                }
                return resposta.json();
            })
            .then((dados) => {
                if (Array.isArray(dados)) {
                    setEmprestimos(dados);
                    setExibirTabela(true);
                } else {
                    setErro(dados.status);
                }
                setProcessado(true);
            })
            .catch((error) => {
                console.error('Erro na busca de empréstimos:', error);
                setProcessado(true);
            });
    }

    useEffect(() => {
        buscarEmprestimos();
    }, []); // Executado uma vez ao montar o componente

    if (erro) {
        return (
            <div>
                <p>Erro ao obter os empréstimos do Backend: {erro.message}</p>
            </div>
        );
    } else if (!processado) {
        return (
            <Spinner animation="border" role="status">
                <span className="visually-hidden">Carregando empréstimos...</span>
            </Spinner>
        );
    } else {
        return (
            <Pagina>
                <div>
                    {exibirTabela ? (
                        <TabelaEmprestimo
                            listaEmprestimos={emprestimos}
                            exibirTabela={setExibirTabela}
                            editarEmprestimo={prepararEmprestimoParaEdicao}
                            excluirEmprestimo={apagarEmprestimo}
                            setModoEdicao={setModoEdicao}
                            buscar={buscarEmprestimos}
                            setEmprestimos={setEmprestimos}
                        />
                    ) : (
                        <FormEmprestimo
                            listaEmprestimos={emprestimos}
                            setEmprestimos={setEmprestimos}
                            exibirTabela={setExibirTabela}
                            buscar={buscarEmprestimos}
                            modoEdicao={modoEdicao}
                            setModoEdicao={setModoEdicao}
                            emprestimo={emprestimoEmEdicao}
                        />
                    )}
                </div>
            </Pagina>
        );
    }
}
