import React, { useState } from 'react';
import { Navbar, Container, Nav, NavDropdown, Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons'; // Ícone de hambúrguer

export default function Menu(props) {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <Navbar expand="lg">
      <Container fluid>
        <Navbar.Brand href="#">
          <Link className='linkDeco Menu' to='/'>Quintal da Leitura</Link>
        </Navbar.Brand>
        <Button variant="outline-success" className='botão' onClick={toggleMenu}>
          <FontAwesomeIcon icon={faBars} /> {/* Ícone de hambúrguer */}
        </Button>
        <Navbar.Collapse id="navbarScroll" className={menuOpen ? 'show' : ''}>
          <Nav className="me-auto my-2 my-lg-0" style={{ maxHeight: '100px' }} navbarScroll>
            <NavDropdown title="Cadastros" id="navbarScrollingDropdown">
              <NavDropdown.Item href="#">
                <Link className='linkDeco' to="/cadastroLivro">Cadastro de Título</Link>
              </NavDropdown.Item>
            </NavDropdown>
            <Nav.Link href="#">
              <Link className='linkDeco' to='/exemplar'>Exemplar</Link>
            </Nav.Link>
            <Nav.Link href="#">
              <Link className='linkDeco' to='/emprestimo'>Empréstimo</Link>
            </Nav.Link>
          </Nav>
          <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Pesquisar"
              className="me-2"
              aria-label="Search"
            />
            <Button variant="outline-success" className='botão'>Pesquisar</Button>
          </Form>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
