import React, { useState } from "react";
import { Button, Container, Table } from "react-bootstrap";
import 'bootstrap-icons/font/bootstrap-icons.css';

export default function TabelaItensEmprestimo(props) {

    const [termoDeBusca, setTermoDeBusca] = useState('');

    return (
        <Container className="m-3 border">
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Código</th>
                        <th>Data de Empréstimo</th>
                        <th>CPF da Pessoa</th>
                        <th>Título Exemplar</th>
                        <th>Item Disponível</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        props.listaEmprestimos?.map((emprestimo, indice) => {
                            return (
                                <tr key={indice} id="colorwhite">
                                    <td id="colorwhite">{emprestimo.codigo}</td>
                                    <td id="colorwhite">{emprestimo.dataEmprestimo}</td>
                                    <td id="colorwhite">{emprestimo.pessoa.cpf}</td>
                                    <td>{emprestimo.exemplar.acervo.titulo}</td>
                                    <td>{emprestimo.itemDisp}</td>
                                    <td>
                                        <Button onClick={() => { props.editarEmprestimo(emprestimo) }}>
                                            <i className="bi bi-pencil"></i>
                                        </Button>{" "}
                                        <Button onClick={() => {
                                            if (window.confirm("Deseja realmente excluir o empréstimo?")) {
                                                props.excluirEmprestimo(emprestimo);
                                            }
                                        }}>
                                            <i className="bi bi-trash"></i>
                                        </Button>
                                    </td>
                                </tr>
                            );
                        })
                    }
                </tbody>
            </Table>
        </Container>
    );
}
