import React, { useEffect, useState } from 'react';
import { Container, Form, FormGroup, Row, Col, Button } from 'react-bootstrap';
import CaixaSelecao from '../componentes/busca/CaixaSelecao';
import { urlBase } from '../utilitarios/definicoes';
import { Link } from 'react-router-dom';
import './FormEmprestimo.css';

export default function Formulario() {
  const [validado, setValidado] = useState(false);
  const [listaPessoa, setListaPessoa] = useState([]);
  const [pessoaSelecionada, setPessoaSelecionada] = useState({});
  const [exemplarSelecionado, setExemplarSelecionado] = useState({});
  const [emprestimo, setEmprestimo] = useState({
    codigo: '',
    dataEmprestimo: '',
    pessoa: {
      cpf: '',
    },
    listaExemplares: [],
    itemDisp: '',
  });

  useEffect(() => {
    fetch(`${urlBase}/pessoa`, {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },
    })
      .then((response) => response.json())
      .then((listaPessoas) => {
        setListaPessoa(listaPessoas);
      })
      .catch((erro) => {
        alert(erro.message);
      });
  }, []);

  function manipularMudanca(evento) {
    const elemForm = evento.currentTarget;
    const id = elemForm.id;
    const valor = elemForm.value;
    setEmprestimo({ ...emprestimo, [id]: valor });
  }

  function gravarEmprestimo() {
    fetch(`${urlBase}/emprestimo`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        codigo: emprestimo.codigo,
        dataEmprestimo: emprestimo.dataEmprestimo,
        pessoa: {
          cpf: emprestimo.pessoa.cpf,
        },
        listaExemplares: emprestimo.listaExemplares,
        itemDisp: emprestimo.itemDisp,
      }),
    })
      .then((resposta) => resposta.json())
      .then((dados) => {
        if (dados.status) {
          setEmprestimo({ ...emprestimo, codigo: dados.codigo });
        }
        alert(dados.mensagem);
      })
      .catch((erro) => {
        alert('Não foi possível registrar o empréstimo: ' + erro.message);
      });
  }

  const manipulaSubmissao = (evento) => {
    const form = evento.currentTarget;
    if (form.checkValidity() === false) {
      setValidado(false);
      gravarEmprestimo();
    } else {
      setValidado(true);
    }
    evento.preventDefault();
    evento.stopPropagation();
  };

  return (
    <Container>
      <Form
        id="formEmprestimo"
        noValidate
        validated={validado}
        onSubmit={manipulaSubmissao}
        className="mainForm"
      >
        <h1 className="text-center colorWhite">Cadastro de Exemplar</h1>
        <Row>
          <Col md={4}>
            <FormGroup>
              <Form.Label htmlFor="codigo" className="form-label">
                Código
              </Form.Label>
              <Form.Control
                type="text"
                className="form-control"
                value={emprestimo.codigo}
                placeholder="Automático"
                id="codigo"
                onChange={manipularMudanca}
                disabled
              />
            </FormGroup>
          </Col>

          <Col md={4}>
            <FormGroup className="mb-3" controlId="dataEmprestimo">
              <Form.Label>Data do Empréstimo</Form.Label>
              <Form.Control
                type="date"
                id="dataEmprestimo"
                onChange={manipularMudanca}
                value={emprestimo.dataEmprestimo}
                required
              />
              <Form.Control.Feedback type="invalid">
                Selecione uma data de Empréstimo válida
              </Form.Control.Feedback>
            </FormGroup>
          </Col>

          <Col md={4}>
            <FormGroup className="mb-3" controlId="itemDisp">
              <Form.Label>Itens Disponíveis</Form.Label>
              <Form.Control
                type="number"
                id="itemDisp"
                onChange={manipularMudanca}
                value={emprestimo.itemDisp}
                required
              />
              <Form.Control.Feedback type="invalid">
                Selecione uma data de Empréstimo válida
              </Form.Control.Feedback>
            </FormGroup>
          </Col>
        </Row>

        <FormGroup className="mb-3" controlId="pessoa">
          <Form.Label>Selecione a Pessoa:</Form.Label>
          <CaixaSelecao
            enderecoFonteDados={`${urlBase}/pessoa`}
            campoChave="cpf"
            campoExibicao="nome"
            funcaoSelecao={setPessoaSelecionada}
            valor={pessoaSelecionada}
            id="pessoa"
            required
          />
        </FormGroup>

        <Container>
          <Row>
            <FormGroup className="mb-3" controlId="exemplar">
              <Form.Label>Selecione o Exemplar:</Form.Label>
              <CaixaSelecao
                eenderecoFonteDados={urlBase + "/exemplar"}
                campoChave={"codigo"}
                campoExibicao={(item) => (
                    <Link to={`/acervo/${item.acervo.codigo}`}>
                        ${item.acervo.codigo} - ${item.acervo.titulo}
                    </Link>
                )}
                funcaoSelecao={setExemplarSelecionado}
                valor={exemplarSelecionado}
                id="exemplar"
                required
              />
            </FormGroup>
          </Row>
        </Container>

        <Row className="mb-3 botao">
          <div className="botao">
            <Button type="submit" className="botao" id="cadastrar"></Button>

            <Button variant="secondary" type="button">
              Voltar
            </Button>
          </div>
        </Row>
      </Form>
    </Container>
  );
}
