import React, { useState } from "react";
import { Button, Container, Table } from "react-bootstrap";
import 'bootstrap-icons/font/bootstrap-icons.css';
import "./estilos/tabela.css";

export default function TabelaItensEmprestimo(props) {

    const [termoDeBusca, setTermoDeBusca] = useState('');
    const [exemplar, setExemplar] = useState([]);

    return (
        <Container className="m-3 border">
            <Table striped bordered hover>
                <thead className="colorwhite">
                    <tr>
                        <th>Data de Empréstimo</th>
                        <th>CPF da Pessoa</th>
                        <th>Título Exemplar</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        props.listaEmprestimos?.map((item, indice) => {
                            return (
                                <tr key={indice} id="colorwhite">
                                    <td id="colorwhite">{item.dataEmprestimo}</td>
                                    <td id="colorwhite">{item.pessoa.cpf}</td>
                                    <td id="colorwhite">{exemplar.acervo.titulo}</td>
                                    <td>
                                        <Button onClick={() => {
                                            const lista = props.listaItens.filter((prod) => prod.codigo !== item.codigo);
                                            props.setEmprestimo({ ...props.dadosEmprestimo, listaProdutos: lista });
                                        }}>
                                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-bag-dash" viewBox="0 0 16 16">
                                                <path fill-rule="evenodd" d="M5.5 10a.5.5 0 0 1 .5-.5h4a.5.5 0 0 1 0 1H6a.5.5 0 0 1-.5-.5z" />
                                                <path d="M8 1a2.5 2.5 0 0 1 2.5 2.5V4h-5v-.5A2.5 2.5 0 0 1 8 1zm3.5 3v-.5a3.5 3.5 0 1 0-7 0V4H1v10a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2V4h-3.5zM2 5h12v9a1 1 0 0 1-1 1H3a1 1 0 0 1-1-1V5z" />
                                            </svg>
                                        </Button>
                                    </td>
                                </tr>
                            );
                        })
                    }
                </tbody>
            </Table>
        </Container>
    );
}
