import React, { useEffect, useState } from 'react';
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import { Row, Col } from 'react-bootstrap';
import { urlBase } from '../utilitarios/definicoes';
import CaixaSelecao from '../componentes/busca/CaixaSelecao';

export default function Formulario() {

    const [validado, setValidado] = useState(false);
    const [listaPessoa, setListaPessoa] = useState([]);
    const [pessoaSelecionada, setPessoaSelecionada] = useState({});
    const [exemplarSelecionado, setExemplarSelecionado] = useState({});
    const [qtdExemplarSelecionado, setQtdExemplarSelecionado] = useState(1);
    const [listaExemplares, setListaExemplares] = useState([]);

    const [emprestimo, setEmprestimo] = useState({
        codigo: "",
        dataEmprestimo: "",
        pessoa: {
            cpf: pessoaSelecionada.cpf
        },
        listaExemplares: [],
        itemDisp: ""
    });



    function manipularMudanca(evento) {
        const elemForm = evento.currentTarget;
        const id = elemForm.id;
        const valor = elemForm.value;
        setEmprestimo({ ...emprestimo, [id]: valor });
    }

    function gravarEmprestimo() {
        fetch(urlBase + "/emprestimo", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                "codigo": emprestimo.codigo,
                "dataEmprestimo": emprestimo.dataEmprestimo,
                "pessoa": {
                    "cpf": emprestimo.pessoa.cpf
                },
                "listaExemplares": emprestimo.listaExemplares,
                "itemDisp": emprestimo.itemDisp
            }),
        })
            .then((resposta) => resposta.json())
            .then((dados) => {
                if (dados.status) {
                    setEmprestimo({ ...emprestimo, codigo: dados.codigo });
                }
                alert(dados.mensagem);
            })
            .catch((erro) => {
                alert("Não foi possivel registrar o emprestimo: " + erro.message);
            });
    }



    const manipulaSubmissao = (evento) => {
        const form = evento.currentTarget;
        if (form.checkValidity() === false) {
            setValidado(false);
            gravarEmprestimo();
        }
        else {
            setValidado(true);
        }
        evento.preventDefault();
        evento.stopPropagation();
    };

    return (
        <body id="corpo">
            <Container className="background mb-3">
                <Form
                    id="formEmprestimo"
                    noValidate
                    validated={validado}
                    onSubmit={manipulaSubmissao}
                    className="mainForm"
                >
                    <h1 className="text-center colorWhite">Cadastro de Exemplar</h1>
                    <Row>
                        <Col md={4}>
                            <Form.Group>

                                <Form.Label htmlFor="codigo" className="form-label">Codigo</Form.Label>
                                <Form.Control
                                    type="text"
                                    className="form-control"
                                    value={emprestimo.codigo}
                                    placeholder="Automático"
                                    id="codigo"
                                    onChange={manipularMudanca} disabled />
                            </Form.Group>
                        </Col>

                        <Col md={4}>
                            <Form.Group className="mb-3" controlId="dataEmprestimo">
                                <Form.Label>Data do Emprestimo</Form.Label>
                                <Form.Control
                                    type="date"
                                    id="dataEmprestimo"
                                    onChange={manipularMudanca}
                                    value={emprestimo.dataEmprestimo}
                                    required
                                />
                                <Form.Control.Feedback type="invalid">
                                    Selecione uma data de Emprestimo válida
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Col>

                        <Col md={4}>
                            <Form.Group className="mb-3" controlId="dataEmprestimo">
                                <Form.Label>Itens Disponivel</Form.Label>
                                <Form.Control
                                    type="number"
                                    id="itemDisp"
                                    value={qtdExemplarSelecionado}
                                    onChange={(e) => {
                                        setQtdExemplarSelecionado(e.target.value)
                                        if (e.target.value > exemplarSelecionado.quantidade) {
                                            alert("Quantidade de exemplares indisponivel")
                                        }
                                    }
                                    }
                                />
                                <Form.Control.Feedback type="invalid">
                                    Selecione uma data de Emprestimo válida
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Col>

                    </Row>
                    <Form.Group className="mb-3" controlId="pessoa">
                        <Form.Label>Selecione o Pessoa:</Form.Label>
                        {/* <BarraBusca
                        campoBusca={"nome"}
                        campoChave={"cpf"}
                        dados={listaPessoa}
                        funcaoSelecao={setPessoaSelecionada}
                        placeHolder={"Selecione uma pessoa"}
                        valor={""}
                        />  */}
                        <CaixaSelecao
                            enderecoFonteDados={urlBase + "/pessoa"}
                            campoChave={"cpf"}
                            campoExibicao={"nome"}
                            funcaoSelecao={setPessoaSelecionada}
                            valor={pessoaSelecionada}
                            id="pessoa"
                            required
                        />
                    </Form.Group>

                    <Container>
                        <Row>
                            <Form.Group className="mb-3" controlId="pessoa">
                                <Form.Label>Selecione o Exemplar:</Form.Label>
                                <CaixaSelecao
                                    enderecoFonteDados={`${urlBase}/exemplar`}
                                    campoChave="codigo"
                                    campoExibicao="codigo"
                                    funcaoSelecao={setExemplarSelecionado}
                                    valor={exemplarSelecionado}
                                    id="exemplar"
                                />
                            </Form.Group>
                        </Row>

                        <Row>
                            <Col md={4}>
                                <Form.Label>Codigo Exemplar</Form.Label>
                                <Form.Control
                                    type="text"
                                    onChange={manipularMudanca}
                                    value={exemplarSelecionado.codigo}
                                    nome="exemplarSelecionado"
                                    disabled
                                    required
                                />
                            </Col>

                            <Col md={4}>
                                <Form.Label>Quantidade</Form.Label>
                                <Form.Control
                                    type="text"
                                    onChange={manipularMudanca}
                                    value={exemplarSelecionado.quantidade}
                                    nome="exemplarSelecionado"
                                    disabled
                                    required
                                />
                            </Col>

                            <Col md={4}>
                                <Form.Label>Data Cadastro</Form.Label>
                                <Form.Control
                                    type="text"
                                    onChange={manipularMudanca}
                                    value={exemplarSelecionado.dataCadastro}
                                    nome="exemplarSelecionado"
                                    disabled
                                    required
                                />
                            </Col>
                        </Row>

                        <Row>
                            <Col>
                                <Form.Label>Titulo</Form.Label>
                                <Form.Control
                                    type="text"
                                    onChange={manipularMudanca}
                                    value={exemplarSelecionado && exemplarSelecionado.acervo && exemplarSelecionado.acervo.titulo}
                                    nome="exemplarSelecionado"
                                    disabled
                                    required
                                />
                            </Col>
                        </Row>

                        

                    </Container>

                    <Row >
                        <div>
                            <Button type="submit" className="botao" >Salvar</Button>
                            <Button
                                variant="secondary"
                                type="button"

                            >Voltar
                            </Button>

                        </div>
                    </Row>

                </Form>
            </Container >
        </body >
    );
}
