import React, { useState } from "react";
import { Button, Table, Container, Row, Col } from "react-bootstrap";
import "./estilos/tabela.css";
import 'bootstrap-icons/font/bootstrap-icons.css';

export default function TabelaEmprestimo(props) {

    const [termoDeBusca, setTermoDeBusca] = useState('');

    return (
        <body id="corpo" className="colorwhite ">
            <Container className="border mb-2 mt-2 corpoTabela" >
                <h2 className="text-center m-4">Empréstimos Cadastrados</h2>
                <Row className='mb-2 mt-2 '>
                    <Col>
                        <Button variant="success"
                            onClick={() => {
                                props.exibirTabela(false);
                                props.setModoEdicao(false);
                            }}>
                            Cadastrar
                        </Button>
                    </Col>
                </Row>

                <Table striped bordered hover className="text-center">
                    <thead className="colorwhite">
                        <tr>
                            <th>Código</th>
                            <th>Data de Empréstimo</th>
                            <th>CPF da Pessoa</th>
                            <th>Lista de Exemplares</th>
                            <th>Item Disponível</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {props.listaEmprestimos?.map((emprestimo, i) => {
                            return (
                                <tr key={i}>
                                    <td id="colorwhite">{emprestimo.codigo}</td>
                                    <td id="colorwhite">{emprestimo.dataEmprestimo}</td>
                                    <td id="colorwhite">{emprestimo.pessoa.cpf}</td>
                                    <td>
                                        <ul>
                                            {emprestimo.listaExemplares.map((exemplar, j) => (
                                                <li key={j}>{exemplar}</li>
                                            ))}
                                        </ul>
                                    </td>
                                    <td>{emprestimo.itemDisp ? "Sim" : "Não"}</td>
                                    <td>
                                        <Button variant="warning" onClick={() => { props.editarEmprestimo(emprestimo) }}>
                                            <i className="bi bi-pencil"></i>
                                        </Button>{" "}
                                        <Button variant="danger"
                                            onClick={() => {
                                                if (window.confirm("Deseja realmente excluir o empréstimo?")) {
                                                    props.excluirEmprestimo(emprestimo);
                                                }
                                            }}
                                        >
                                            <i className="bi bi-trash"></i>
                                        </Button>
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </Table>
            </Container>
        </body>
    );
}
