import React, { useEffect, useState } from 'react';
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import { Row, Col } from 'react-bootstrap';
import BarraBusca from "../componentes/busca/BarraBusca.jsx"
import { urlBase } from '../utilitarios/definicoes';
import { set } from 'react-hook-form';
import CaixaSelecao from '../componentes/busca/CaixaSelecao';

export default function Formulario() {

    const [validado, setValidado] = useState(false);
    const [listaPessoas, setListaPessoas] = useState([]);
    const [pessoaSelecionada, setPessoaSelecionada] = useState({});

    const [emprestimo, setEmprestimo] = useState({
        codigo: "",
        dataEmprestimo: "",
        pessoa: {
            cpf: pessoaSelecionada.cpf
        },
        listaExemplares: [],
        itemDisp: true
    });

    useEffect(() => {
        fetch(urlBase + "/pessoa", {
            method: "GET",
            headers: { "Content-Type": "application/json" },
        })
        .then((listaPessoas) => {
            setListaPessoas(listaPessoas); 
        })
        .catch((erro) => {
            alert(erro.message);
        });
    }, []);
    

    function manipularMudanca(evento) {
        const elemForm = evento.currentTarget;
        const id = elemForm.id;
        const valor = elemForm.value;
        setEmprestimo({ ...emprestimo, [id]: valor });
    }

    function gravarEmprestimo(){
        fetch(urlBase + "/emprestimo", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                "codigo": emprestimo.codigo,
                "dataEmprestimo": emprestimo.dataEmprestimo,
                "pessoa": {
                    "cpf": emprestimo.pessoa.cpf
                },
                "listaExemplares": emprestimo.listaExemplares,
                "itemDisp": emprestimo.itemDisp
            }),
        })
        .then((resposta) => resposta.json())
        .then((dados) => {
            if (dados.status){
                setEmprestimo({...emprestimo, codigo: dados.codigo});
            }
            alert(dados.mensagem);
        })
        .catch((erro) => {
            alert("NÃ£o foi possivel registrar o emprestimo: "+erro.message);
        });
    }

    const manipulaSubmissao = (evento) => {
        const form = evento.currentTarget;
        if (form.checkValidity() === false) {
            setValidado(false);
            gravarEmprestimo();
        }
        else{
            setValidado(true);
        }
        evento.preventDefault();
        evento.stopPropagation();
    };

    return (
        <body id="corpo">
            <Container className="background mb-3">
                <Form
                    id="formEmprestimo"
                    noValidate
                    validated={validado}
                    onSubmit={manipulaSubmissao}
                    className="mainForm"
                >
                    <h1 className="text-center colorWhite">Cadastro de Exemplar</h1>
                    <Row>
                        <Col md={4}>
                            <Form.Group>

                                <Form.Label htmlFor="codigo" className="form-label">Codigo</Form.Label>
                                <Form.Control
                                    type="text"
                                    className="form-control"
                                    value={emprestimo.codigo}
                                    placeholder="AutomÃ¡tico"
                                    id="codigo"
                                    onChange={manipularMudanca} disabled />
                            </Form.Group>
                        </Col>

                        <Col md={4}>
                            <Form.Group className="mb-3" controlId="dataEmprestimo">
                                <Form.Label>Data do Emprestimo</Form.Label>
                                <Form.Control
                                    type="date"
                                    id="dataEmprestimo"
                                    onChange={manipularMudanca}
                                    value={emprestimo.dataEmprestimo}
                                    required
                                />
                                <Form.Control.Feedback type="invalid">
                                    Selecione uma data de Emprestimo vÃ¡lida
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Col>

                        <Col md={4}>
                            <Form.Group className="mb-3" controlId="dataEmprestimo">
                                <Form.Label>Itens Disponivel</Form.Label>
                                <Form.Control
                                    type="number"
                                    id="itemDisp"
                                    onChange={manipularMudanca}
                                    value={emprestimo.itemDisp}
                                    required
                                />
                                <Form.Control.Feedback type="invalid">
                                    Selecione uma data de Emprestimo vÃ¡lida
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Col>

                    </Row>
                    <Form.Group className="mb-3" controlId="pessoa">
                        <Form.Label>Selecione o Pessoa:</Form.Label>
                        {/* <BarraBusca
                        campoBusca={"nome"}
                        campoChave={"cpf"}
                        dados={listaPessoas}
                        funcaoSelecao={pessoaSelecionada}
                        placeHolder={"Selecione uma pessoa"}
                        valor={""}
                        />  */}
                        <CaixaSelecao 
                                     enderecoFonteDados={urlBase + "/pessoa"}
                                     campoChave={"cpf"}
                                     campoExibicao={"nome"}
                                     funcaoSelecao={setPessoaSelecionada}
                                     valor={pessoaSelecionada}
                                     id="acervo"
                                     required
                        />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="exemplares">
                        <Form.Label>Selecione o Exemplares:</Form.Label>


                    </Form.Group>


                </Form>
            </Container>
        </body>
    );
}
